# 1 задание age_1 = 27 name_1 = 'Andrei'print ('Меня зовут {}. Мне {} лет'.format(name_1, age_1))name_2 = input ('Как тебя зовут? - ')age_2 = int (input('И сколько тебе лет? - '))print ('Тебе {} лет. И тебя зовут {}. Приятно познакомиться!'.format (age_2,                                                                      name_2))# 2 заданиеsec = int (input ('Введите время в секундах - '))h = sec//3600m = (sec - h*3600)//60sec -= h*3600 + m*60print ('Время в стандартном формате - {}:{}:{}.'.format(h,m,sec))# 3 заданиеn = input ('Введите число n - ')sum = int(n) + int(2*n) + int (3*n) # сумма n + nn + nnnprint(sum)# 4 заданиеn = int(input('Введите целое положительное число n - '))maximum = 0 while n > 0:    last = n%10    n = n//10    if last > maximum:        maximum = lastprint ('Самая большая цифра в числе - {}'.format(maximum))# 5 заданиеearnings = int (input ('Введите выручку фирмы в млн.руб. - '))costs = int (input ('Введите издержки фирмы в млн.руб. - '))if earnings > costs:    print ('Фирма работает с прибылью')    efficiency = ((earnings - costs)/earnings)*100    print ('Рентабельность выручки составила - {}%.'.format (efficiency))    num = int (input ('Введите численность сотрудников - '))    print ('Прибыль фирмы в расчете на одного сотрудника - {} млн. руб.'.           format ((earnings - costs)/num))else:    print ('Фирма работает в убыток')# 6 заданиеa = int (input ('Сколько спортсмен пробежал в первый день в км.? - '))b = int (input ('Сколько спортсмен планирует пробегать через '                 'несколько дней плановых тренировок в км.? - '))day = 1while True:    a += a*0.1    day += 1    if a>b:        breakprint ('На {}-й день результат спортсмена составит {} км.'. format (day,b))